import React from "react";
import "../css/Main.css";
import { Helmet } from "react-helmet";
import Background from "../resources/cityscape_fin.png";
import Tone from "tone";

export const Synthesizer = () => {
  const synth = new Tone.Synth().toDestination();
const now = Tone.now()

// volume control variable


// sine tone set by default
let wavePicker = document.querySelector("select[name='waveform']");
let type = wavePicker.options[wavePicker.selectedIndex].value;
synth.oscillator.type = type;

// connect it to the master output (your speakers)
synth.toMaster();

const piano = document.getElementById("piano");

piano.addEventListener("mousedown", e => {
  // change waveform when you click on the piano
  let wavePicker = document.querySelector("select[name='waveform']");
  let type = wavePicker.options[wavePicker.selectedIndex].value;
  synth.oscillator.type = type;
  // fires off a note continously until trigger is released
  synth.triggerAttack(e.target.dataset.note);
});

piano.addEventListener("mouseup", e => {
  // stops the trigger
  synth.triggerRelease();
});

// handles keyboard events
document.addEventListener("keydown", e => {

  // change waveform when you press on keyboard
  let wavePicker = document.querySelector("select[name='waveform']");
  let type = wavePicker.options[wavePicker.selectedIndex].value;
  synth.oscillator.type = type;

  // e object has the key property to tell which key was pressed
  switch (e.key) {
      // LEFT ROW
      case "z":
        return synth.triggerAttack("C3", now);
      case "s":
        return synth.triggerAttack("C#3", now);
      case "x":
        return synth.triggerAttack("D3", now);
      case "d":
        return synth.triggerAttack("D#3", now);
      case "c":
        return synth.triggerAttack("E3", now);
      case "v":
        return synth.triggerAttack("F3", now);
      case "g":
        return synth.triggerAttack("F#3", now);
      case "b":
        return synth.triggerAttack("G3", now);
      case "h":
        return synth.triggerAttack("G#3", now);
      case "n":
        return synth.triggerAttack("A3", now);
      case "j":
        return synth.triggerAttack("A#3", now);
      case "m":
        return synth.triggerAttack("B3", now);

    // RIGHT ROW
    case "q":
      return synth.triggerAttack("C4", now);
    case "2":
      return synth.triggerAttack("C#4", now);
    case "w":
      return synth.triggerAttack("D4", now);
    case "3":
      return synth.triggerAttack("D#4", now);
    case "e":
      return synth.triggerAttack("E4", now);
    case "r":
      return synth.triggerAttack("F4", now);
    case "5":
      return synth.triggerAttack("F#4", now);
    case "t":
      return synth.triggerAttack("G4", now);
    case "6":
      return synth.triggerAttack("G#4", now);
    case "y":
      return synth.triggerAttack("A4", now);
    case "7":
      return synth.triggerAttack("A#4", now);
    case "u":
      return synth.triggerAttack("B4", now);
    default:
      return;
  }
});
// when the key is released, audio is released as well
document.addEventListener("keyup", e => {
  switch (e.key) {

    // LEFT ROW
    case "z":
    case "s":
    case "x":
    case "d":
    case "c":
    case "v":
    case "g":
    case "b":
    case "h":
    case "n":
    case "j":
    case "m":

    /// RIGHT ROW
    case "q":
    case "2":
    case "w":
    case "3":
    case "e":
    case "r":
    case "5":
    case "t":
    case "6":
    case "y":
    case "7":
    case "u":
       synth.triggerRelease();
  }
});
  return (
    <>
      <div class="main">
        <div class="screen">

          <table class="settings_table">
            <td colspan="2" class="synth_sity_image_cell">
              <img
                class="synth_sity_image"
                alt=""
                src={Background}
                width="600px"
              />
            </td>
            <tr>
              <td colspan="2" class="synth_sity_title">
                <h1>SynthSityX v1.0</h1>
              </td>
            </tr>
            <tr>
              <td class="setting_cell">
                <div class="center">
                  <span>WAVEFORM</span>
                  <br />
                  <br />
                  <select name="waveform" id="waveform_forum">
                    <option value="sine">SINE</option>
                    <option value="square">SQUARE</option>
                    <option value="sawtooth">SAWTOOTH</option>
                    <option value="triangle">TRIANGLE</option>
                  </select>
                </div>
              </td>

              <td class="setting_cell">
                <div class="settingsBar">
                  <div class="center">
                    <span>VOLUME</span>
                    <br />
                    <input
                      class="volume_bar"
                      type="range"
                      min="0.0"
                      max="1.0"
                      step="0.01"
                      value="0.5"
                      list="volumes"
                      name="volume"
                    />
                    <datalist id="volumes">
                      <option value="0.0" label="Mute" />
                      <option value="1.0" label="100%" />
                    </datalist>
                  </div>
                </div>
              </td>
            </tr>
          </table>

          <div class="keyboard">
            <ul id="piano" class="piano">
              <li data-note="C3" class="key">
                <div data-note="C#3" class="black-key">
                  S
                </div>
                Z
              </li>
              <li data-note="D3" class="key">
                <div data-note="D#3" class="black-key">
                  D
                </div>
                X
              </li>
              <li data-note="E3" class="key">
                C
              </li>
              <li data-note="F3" class="key">
                <div data-note="F#3" class="black-key">
                  G
                </div>
                V
              </li>
              <li data-note="G3" class="key">
                <div data-note="G#3" class="black-key">
                  H
                </div>
                B
              </li>
              <li data-note="A3" class="key">
                <div data-note="A#3" class="black-key">
                  J
                </div>
                N
              </li>
              <li data-note="B4" class="key">
                M
              </li>

              <li data-note="C4" class="key">
                <div data-note="C#4" class="black-key">
                  2
                </div>
                Q
              </li>
              <li data-note="D4" class="key">
                <div data-note="D#4" class="black-key">
                  3
                </div>
                W
              </li>
              <li data-note="E4" class="key">
                E
              </li>
              <li data-note="F4" class="key">
                <div data-note="F#4" class="black-key">
                  5
                </div>
