import React, { Component } from "react";
import "../css/Main.css";

class Login extends Component {
	constructor(props) {
        super(props);
		this.option = 1;
        this.state = {
            redirect: false,
            email: '',
            password: '',
			firstName: '',
			lastName: '',
			phoneNumber: '',
			creds: {}
		};
    };

/* handleSubmit: Handle when the user click on the submit button.*/
  async handleSubmitSignUp(event){
	if(this.state.email.trim() === '' || this.state.password.trim() === '' || this.state.firstName.trim() === '' || this.state.lastName.trim() === '' || this.state.phoneNumber.trim() === '' ){
		alert("Please make sure all the fields are filled.")
	}
	else {
		//fetch API to get user credential
		 await fetch('login/' + this.state.email)
			.then(res => res.json())
			.then(creds => this.setState({creds}));
			//if the email is not in the databse or wrong password
		if (this.state.creds !== null) {
			alert("Email is already used.")
		}
		else{
			await fetch('login/', {
					method: 'POST',
					headers: { 'Content-Type': 'application/json' },
					body: JSON.stringify(
						{
							email: this.state.email,
							password:this.state.password,
							firstName: this.state.firstName,
							lastName: this.state.lastName,
							phoneNumber: this.state.phoneNumber
						})
					})
					.then(res => res.json())
					.then(body => console.log(body));
					this.setState({redirect: true});
		}
		UserProfile.setName(this.state.email);
		UserProfile.setSession(true);
		event.preventDefault();
	}
  }

  Form({ option }) {
    return (
      <form className="account-form" onSubmit={(evt) => evt.preventDefault()}>
        <div
          className={
            "account-form-fields " + (option === 1 ? "sign-in" : "sign-up")
          }
        >
          <input
            id="email"
            name="email"
            type="email"
            placeholder="E-mail"
            required
          />
          <input
            id="password"
            name="password"
            type="password"
            placeholder="Password"
            required
          />
        </div>
        <button className="btn-submit-form" type="submit">
          Sign in
        </button>
      </form>
    );
  }

  Create({ option }) {
    return (
      <form className="account-form" onSubmit={(evt) => evt.preventDefault()}>
        <div
          className={
            "account-form-fields " + (this.state.option === 1 ? "sign-in" : "sign-up")
          }
        >
          <input
            id="firstName"
            name="firstName"
            type="firstName"
            placeholder="First Name"
            required
          />
          <input
            id="lastName"
            name="lastName"
            type="lastName"
            placeholder="Last Name"
            required
          />
          <input
            id="email"
            name="email"
            type="email"
            placeholder="E-mail"
            required
          />
          <input
            id="password"
            name="password"
            type="password"
            placeholder="Password"
            required
          />
          <input
            id="phoneNumber"
            name="phoneNumber"
            type="phoneNumber"
            placeholder="Phone Number"
            required
          />
        </div>
        <button className="btn-submit-form" type="submit">
          Sign up
        </button>
      </form>
    );
  }

  render() {
    return (
      <div className="container">
        <header>
          <div
            className={
              "header-headings " + (this.state.option === 1 ? "sign-in" : "sign-up")
            }
          >
            <span>Sign in to your account</span>
            <span>Create an account</span>
          </div>
        </header>
        <ul className="options">
		<li className={this.state.option === 1 ? 'active' : ''} onClick={() => this.setState({option: 1})}>Sign in</li>
		<li className={this.state.option === 2 ? 'active' : ''} onClick={() => this.setState({option: 2})}>Sign up</li>
        </ul>
        {this.state.option === 1 ? this.Form() : this.Create()}
      </div>
    );
  }
}

export default Login;
