import React, { Component, useContext } from "react";
import { Navigate } from "react-router-dom";
import { GlobalContext } from "../context/GlobalState";
import  UserProfile  from "../scripts/UserProfile"
import "../css/Main.css";

class Login extends Component {
  static contextType = GlobalContext
  constructor(props) {
    super(props);
    this.state = {
      option: 1,
      redirect: false,
      email: "",
      password: "",
      firstName: "",
      lastName: "",
      creds: {},
    };
    this.handleChange = this.handleChange.bind(this);
  }
  /* handleSubmit: Handle when the user click on the submit button.*/
  async handleSubmitSignUp(event) {
    if (
      this.state.email.trim() === "" ||
      this.state.password.trim() === "" ||
      this.state.firstName.trim() === "" ||
      this.state.lastName.trim() === ""
    ) {
      alert("Please make sure all the fields are filled.");
    } else {
      await fetch("login/" + this.state.email, {
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
      })
        .then((res) => res.json())
        .then((creds) => this.setState({ creds }));
      //if the email is not in the databse or wrong password
      if (this.state.creds !== null) {
        alert("Email is already used.");
      } else {
        await fetch("login/", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            email: this.state.email,
            password: this.state.password,
            firstName: this.state.firstName,
            lastName: this.state.lastName,
          }),
        })
          .then((res) => res.json())
          .then((body) => console.log(body));
        this.setState({ redirect: true });
      }
      this.context.login(true);
      UserProfile.setName(this.state.email);
      event.preventDefault();
    }
  }

  async handleSubmit(event) {
    event.preventDefault();
    //if the inputs are empty
    if (this.state.email === "" || this.state.password === "") {
      alert("Please make sure both email and password are filled out.");
    } else {
      //fetch API to get user credential
      await fetch("login/" + this.state.email)
        .then((res) => res.json())
        .then((creds) => this.setState({ creds }));
      //if the email is not in the database or wrong password
      if (
        this.state.creds === null ||
        this.state.password !== this.state.creds.password
      ) {
        alert("No account under this email.");
      } else {
        //all the credential information is right, redirect to the homepage
        this.context.login(true);
        UserProfile.setName(this.state.email);
        this.setState({ redirect: true });
      }
    }
  }

  handleChange = ({ target }) => {
    this.setState({ [target.name]: target.value });
  };
  renderRedirect = () => {
    if (this.state.redirect) {
      return <Navigate to="/profile" />;
    }
  };

  form() {
    return (
      <form className="account-form" onSubmit={(evt) => evt.preventDefault()}>
        <hr />
        <div
          className={
            "account-form-fields " +
            (this.state.option === 1 ? "sign-in" : "sign-up")
          }
        >
          <input
            id="email"
            name="email"
            type="email"
            placeholder="E-mail"
            value={this.state.email}
            onChange={this.handleChange}
            required
          />
          <input
            id="password"
            name="password"
            type="password"
            placeholder="Password"
            value={this.state.password}
            onChange={this.handleChange}
            required
          />
        </div>
        <button
          className="btn-submit-form"
          type="submit"
          onClick={(e) => this.handleSubmit(e)}
        >
          Sign in
        </button>
        {this.renderRedirect()}
      </form>
    );
  }

  create() {
    return (
      <form className="account-form" onSubmit={(evt) => evt.preventDefault()}>
        <hr />
        <div
          className={
            "account-form-fields " +
            (this.state.option === 1 ? "sign-in" : "sign-up")
          }
        >
          <input
            id="firstName"
            name="firstName"
            type="text"
            placeholder="First Name"
            value={this.state.firstName}
            onChange={this.handleChange}
            required
          />
          <input
            id="lastName"
            name="lastName"
            type="text"
            placeholder="Last Name"
            value={this.state.lastName}
            onChange={this.handleChange}
            required
          />
          <input
            id="email"
            name="email"
            type="email"
            placeholder="E-mail"
            value={this.state.email}
            onChange={this.handleChange}
            required
          />
          <input
            id="password"
            name="password"
            type="password"
            placeholder="Password"
            value={this.state.password}
            onChange={this.handleChange}
            required
          />
        </div>
        <button
          className="btn-submit-form"
          type="submit"
          onClick={(e) => this.handleSubmitSignUp(e)}
        >
          Sign up
        </button>
        {this.renderRedirect()}
      </form>
    );
  }

  render() {
    return (
      <div className="container">
    
        <header className="marginFromTop">
          <div
            className={
              "header-headings " +
              (this.state.option === 1 ? "sign-in" : "sign-up")
            }
          >
            <span>Sign in to your account</span>
            <span>Create an account</span>
          </div>
        </header>
        <ul className="options">
          <li
            className={this.state.option === 1 ? "active" : ""}
            onClick={() => this.setState({ option: 1 })}
          >
            Sign in
          </li>
          <li
            className={this.state.option === 2 ? "active" : ""}
            onClick={() => this.setState({ option: 2 })}
          >
            Sign up
          </li>
        </ul>
        {console.log(this.context)}
        {this.state.option === 1 ? this.form() : this.create()}
      </div>
    );
  }
}

export default Login;
